import "./Header.scss";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faMagnifyingGlass, faSort, faHeart } from '@fortawesome/free-solid-svg-icons'
import { menuData } from "./menu";
import { useState } from "react";

const Header = () => {
  const [sections, setSections] = useState("none")
  return <header>
    <div className="header-content">
      <div className="header-top-content">
        <div className="logo">
          <a href="/"><img src="/logo1.svg" alt="" /></a>
        </div>
        <div className="search">
          <input className="search-input" type="text" placeholder="Որոնում․․․" onChange={(e) => {

          }}/>
          <div className="all-sections" onClick={() => {

          }}>
            <p>Բոլոր բաժինները</p>
            <div className="arrows">
              <FontAwesomeIcon icon={faSort} />
              <div style={{}} className="sections">
               
              </div>
            </div>
            <div className="search-logo">
              <FontAwesomeIcon icon={faMagnifyingGlass} />
            </div>
          </div>
        </div>
        <div className="heart">
          <FontAwesomeIcon icon={faHeart}/>
        </div>
      </div>
        <div className="header-bottom-content">
            <div className="menu">
              {
                  menuData.map(({name}) => {
                      return (
                        <a href="/">{name}</a>
                      )
                   
                  })
              }
            </div>
        </div>
    </div>
  </header>;
};

export default Header;
