import "./Header.scss";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faMagnifyingGlass, faSort, faHeart } from '@fortawesome/free-solid-svg-icons'
import { menuData } from "./menu";
import { useState, useEffect, useRef } from "react";

const Header = () => {
  const [sections, setSections] = useState("none")
  const [isModalOpen, setIsModalOpen] = useState(false);
   const modalRef = useRef(null)

     useEffect(() => {
    function handleClickOutside(event) {
      if (modalRef.current && !modalRef.current.contains(event.target)) {
        closeModal();
      }
    }

     const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

    if (isModalOpen) {
      // Add an event listener to the document to handle clicks outside the modal
      document.addEventListener('click', handleClickOutside);
    }

    return () => {
      // Remove the event listener when the component unmounts
      document.removeEventListener('click', handleClickOutside);
    };
  }, [isModalOpen])

  return <header>
    <div className="header-content">
      <div className="header-top-content">
        <div className="logo">
          <a href="/"><img src="/logo1.svg" alt="" /></a>
        </div>
        <div className="search">
          <input className="search-input" type="text" placeholder="Որոնում․․․" onChange={(e) => {

          }}/>
          <div className="all-sections" >
            <div className="arrows" onClick={({openModal}) => {
            if (sections === "none") {
              setSections("flex")
            }else{
              setSections("none")
            }
          }}>
            <p>Բոլոր բաժինները</p>
              <FontAwesomeIcon  icon={faSort} />
              <div ref={modalRef} style={{display:sections}} className="sections">
                <p>barev</p>
              </div>
            </div>
            <div className="search-logo">
              <FontAwesomeIcon icon={faMagnifyingGlass} />
            </div>
          </div>
        </div>
        <div className="heart">
          <FontAwesomeIcon icon={faHeart}/>
        </div>
      </div>
        <div className="header-bottom-content">
            <div className="menu">
              {
                  menuData.map(({name}) => {
                      return (
                        <a href="/">{name}</a>
                      )
                   
                  })
              }
            </div>
        </div>
    </div>
  </header>;
};

export default Header;
